mysql -uroot -pnutanix/4u << EOF
-- Create the database user
CREATE USER 'webusr'@'%' IDENTIFIED BY '@@{MariaDB_Root_Password}@@';

-- Create the database
CREATE DATABASE test;

-- Provide access to the database to the webusr
GRANT ALL PRIVILEGES ON test.* to 'webusr'@'%';

-- Create the table for the test data
CREATE TABLE test.tbl_test(id INT(6) AUTO_INCREMENT UNIQUE,firstname VARCHAR(50),lastname VARCHAR(50),email VARCHAR(50));

-- Insert data into the table
INSERT INTO test.tbl_test (firstname,lastname,email)VALUES('test1','test1','test1@test.com'),('test2','test2','test2@test.com'), ('test3','test3','test3@test.com');
EOF

